import react from 'react'
import { } from 'antd';
import styled from 'styled-components';
import LolPostCard from '../components/LolPostCard';
import { useDispatch, useSelector } from 'react-redux';
import { getAllLoLPostRequest } from '../reducers/post';

const PostListForm = () => {
    const {userId} = useSelector((state) => state.user);
    const dispatch = useDispatch();
    const postRequest = () => {
        dispatch(getAllLoLPostRequest());
        console.log("일단");
    }

    const { posts } = useSelector((state) => state.post);   
    return (
        <>
            {userId
                ? posts.map((c) => { return (<div onLoad = {postRequest}><LolPostCard key={c.id} post={c} /></div>); })
                : <div>로그인이 필요합니다.</div>
            }
        </>
    );
}



export default PostListForm
