import { Button } from 'antd';
import React, { useEffect } from 'react';
import AppLayout from '../components/AppLayout';
import Link from 'next/link';
import { useSelector, useDispatch } from 'react-redux';
import SignUpForm from '../components/SignUpForm';
import { LOAD_ALL_LOL_POSTS_REQUEST } from '../reducers/post';


const Home = () => {
    const dispatch = useDispatch();
    const { me } = useSelector((state) => state.user);
    const { logInError } = useSelector((state) => state.user);
    const { posts, loadPostLoading, loadPostError } = useSelector((state) => state.post);

    useEffect(() => {
       if(me){
            dispatch({
                type: LOAD_ALL_LOL_POSTS_REQUEST,
                data: me.userToken,
            })
       }
    });


    if (logInError === -401) {
        return (
            <AppLayout>
                <SignUpForm />
            </AppLayout>
        )
    } else {
        return (
            <AppLayout>
                {me.userId
                    ? posts.map((c) => {
                        return (
                            <div>
                                <Button type="primary" style={{ marginLeft: '20px' }}><Link href="./lol_write"><a>글쓰기</a></Link></Button>
                                <br />
                                <LolPostCard key={c.id} post={c} />
                            </div>);
                    })
                    : <div>로그인이 필요합니다.</div>
                }
            </AppLayout>
        );
    }
};

export default Home;
