import { Button, Pagination, Row, Col } from 'antd';
import React, { useEffect, useCallback, useState } from 'react';
import AppLayout from '../components/AppLayout';
import Link from 'next/link';
import { useSelector, useDispatch } from 'react-redux';
import SignUpForm from '../components/SignUpForm';
import { loadAllLolPostsRequestAction } from '../reducers/post';
import PostCard from '../components/LolPostCard'
import DetailPost from '../components/DetailPost';
import ButtonGroup from 'antd/lib/button/button-group';
import LolFilterForm from '../components/LolFilterForm'
import styled from 'styled-components';
import LOGO from '../images/WEB_LOGO.png'
import APP_STORE from '../images/APP_STORE.png'
import PLAY_STORE from '../images/PLAY_STORE.png'

const Background = styled.div`
    width: 100%;
    height: 100vh;
    background-color: #FA5A5A
`

const Index = () => {
        return (
               <Background>
                   <Row>
                <Col xs={24} md={12} >
                </Col>

                <Col xs={24} md={12} >
                <div style={{position:'relative'}}>
                        <img src={LOGO} style={{height: "100vh", position:'absolute', right: 0}}></img>
                        <img src={APP_STORE} style={{float: 'right', position:'absolute'}}></img>
                        <img src={PLAY_STORE} style={{float: 'right', position:'absolute'}}></img>
                    </div>
                </Col>
            </Row>

                </Background>

        );
};

export default Index;
