import { all, fork, put, takeLatest } from "redux-saga/effects";
import axios from 'axios';
import { useSelector } from 'react-redux';


function socailLogInAPI() {
    return axios.post(
        `http://ec2-18-222-143-156.us-east-2.compute.amazonaws.com:3000/auth/kakao`,)
}


function* logIn(action) {
    try {
        const result = yield call(socailLogInAPI);
        
        console.log("된다");

        // yield put({
        //     type: 'LOG_IN_SUCCESS',
        //     data: action.data,
        // });

    } catch (err) {
        console.log("안된다.");
        // yield put({
        //     type: 'LOG_IN_FAILURE',
        //     error: action.response.data,
        // });
    }
}

function* watchLogIn() {
    yield takeLatest('SOCIAL_LOG_IN_REQUEST', logIn);
}

export default function* userSaga() {
    yield all([
        fork(watchLogIn),
    ]);
}
