export const getPostRequest = (data) => {
    return {    
        type : 'GET_ALL_POST_REQUEST',
        data,
    }
}


const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'GET_ALL_POST_REQUEST':
            return {
                ...state,
                isLoggedIn: true,
                userId: action.data.userId,
                userNickname: action.data.userNickname,
            };
        case 'LOG_OUT':
            return {
                ...state,
                isLoggedIn: false,
                userId: null,
                userNickname: null,
            };
        default:
            return state;
    }
};

export default reducer;
