import { all, fork, put, takeLatest, select, call } from "redux-saga/effects";
import axios from 'axios';

function socialLoginAPI(data) {

    return axios.post(`http://ec2-18-222-143-156.us-east-2.compute.amazonaws.com:3000/auth/${data.platform}`,
    {}, {headers: {Authorization: data.accessToken}});

}


function* logIn(action) {
    try {

        const result = yield call(socialLoginAPI, action.data);

        console.log(result);
        yield put({
            type: 'LOG_IN_SUCCESS',
            data: result.data
        });
    } catch (err) {

        console.error(err);
        // yield put({
        //     type: 'LOG_IN_FAILURE',
        //     error: action.response.data,
        // });

        
    }
}

function* watchLogIn() {
    yield takeLatest('SOCIAL_LOG_IN_REQUEST', logIn);
}

export default function* userSaga() {
    yield all([
        fork(watchLogIn),
    ]);
}
