import react from 'react'
import { } from 'antd';
import styled from 'styled-components';
import LolPostCard from '../components/LolPostCard';
import { useDispatch, useSelector, useEffect } from 'react-redux';
import { getAllLoLPostRequest } from '../reducers/post';

const PostListForm = () => {
    const { userId } = useSelector((state) => state.user);
    const { posts } = useSelector((state) => state.post);
    const dispatch = useDispatch();
    const postRequest = () => {
        dispatch(getAllLoLPostRequest());
        console.log("일단");
    }

    useEffect(() => {
        function onScroll() {
            if (window.pageYOffset + document.documentElement.clientHeight > document.documentElement.scrollHeight - 300) {
                if (hasMorePosts && !loadPostsLoading) {
                    const lastId = mainPosts[mainPosts.length - 1]?.id;
                    dispatch({
                        type: LOAD_POSTS_REQUEST,
                        lastId,
                    });
                }
            }
        }
        window.addEventListener('scroll', onScroll);
        return () => {
            window.removeEventListener('scroll', onScroll);
        };
    }, [hasMorePosts, loadPostsLoading, mainPosts]);


    return (
        <>
            {userId
                ? posts.map((c) => { return (<div onLoad={postRequest}><LolPostCard key={c.id} post={c} /></div>); })
                : <div>로그인이 필요합니다.</div>
            }
        </>
    );
}



export default PostListForm
