    } catch (err) {
        const { accessToken } = useSelector((state) => state.user)

        console.log("안된다.");
        // yield put({
        //     type: 'LOG_IN_FAILURE',
        //     error: action.response.data,
        // });
    }
}

function* watchLogIn() {
    yield takeLatest('SOCIAL_LOG_IN_REQUEST', logIn);
}

export default function* userSaga() {
    yield all([
        fork(watchLogIn),
    ]);
}
