import React, { useCallback, useState } from 'react';
import { List, Card, Button } from 'antd';
import styled from 'styled-components';
import DetailPost from './DetailPost';
import Meta from 'antd/lib/card/Meta';
import { ArrowDownOutlined } from '@ant-design/icons';

const PostCard = styled(Meta)`
    width: 300px;
    height: 200px;
`;

const ListItem = styled(List.Item)`
    height: 250px;
`;



const LolPostList = ({ data }) => {

    const [more, setMore] = useState(false);
    const onClickMore = useCallback(() => {
        setMore(true);

    }, []);
    const onClickClose = useCallback(() => {
        setMore(false);

    }, []);

    return (
        <>{more ? <DetailPost onClose={onClickClose} /> : <List
            style={{ marginBottom: 10 }}
            grid={{ gutter: 1, xs: 1, md: 1 }}
            size="large"
            header="글 목록"
            pagination={{
                onChange: page => {

                },
                pageSize: 12,
            }}
            bordered
            dataSource={data}
            renderItem={(item) => (
                <ListItem>
                    <Card
                        actions={[<ArrowDownOutlined onCLick={ onClickMore } > 자세히 보기</ArrowDownOutlined>]}
                    >
                    <PostCard
                        title={item.title}
                        description={item.content}
                    />
                    </Card>
                    {/* <PostCard title={item.title} actions={["자세히보기"]} onClick={onClickMore}>{item.content}</PostCard> */}
                </ListItem>
    )
}
/>}

        </>
    );
};


export default LolPostList;
