import React, { useState, useCallback } from 'react';
import { Button } from 'antd';
import KakaoLogin from 'react-kakao-login';
import GoogleLogin from 'react-google-login';
import NaverLogin from 'react-naver-login';
import Link from 'next/link';
import styled from 'styled-components';


const LoginFrom = () => {

    // const [id, setId] = useState('');

    const responseKakao = (response) => {
        console.log(response.response.access_token);
    };

    const responseNaver = (response) => {
        console.log(response);
    };

    const responseGoogle = (response) => {
        console.log(response);
    };

    const responseFail = (err) => {
        console.error(err);
    };


    const KakaoBtn = styled(KakaoLogin)`
        padding: 0;
        width: 190px;
        height: 44px;
        line-height: 44px;
        color: #783c00;
        background-color: #FFEB00;
        border: 1px solid transparent;
        border-radius: 3px;
        font-size: 16px;
        font-weight: bold;
        text-align: center;
        cursor: pointer;
        &:hover{
            box-shadow: 0 0px 15px 0 rgba(0, 0, 0, 0.2)
        }        
    `;

    const NaverBtn = styled(NaverLogin)`
        padding: 0;
        width: 190px;
        height: 44px;
        line-height: 44px;
        color: #000000;
        background-color: #00ff04;
        border: 1px solid transparent;
        border-radius: 3px;
        font-size: 16px;
        font-weight: bold;
        text-align: center;
        cursor: pointer;
        &:hover{
            box-shadow: 0 0px 15px 0 rgba(0, 0, 0, 0.2)
        }     
    `;

    const GoogleBtn = styled(GoogleLogin)`
        padding: 0;
        width: 190px;
        height: 44px;
        line-height: 44px;
        color: #ff4400;
        background-color: #000000;
        border: 1px solid transparent;
        border-radius: 3px;
        font-size: 16px;
        font-weight: bold;
        text-align: center;
        cursor: pointer;
        &:hover{
            box-shadow: 0 0px 15px 0 rgba(0, 0, 0, 0.2)
        }        
    `;


    return (
        <div>
            <KakaoBtn
                jsKey={'593246069c7b7a42344710a951c039ee'}
                buttonText="카카오로그인"
                onSuccess={responseKakao}
                onFailure={responseFail}
                getProfile={true}
            ></KakaoBtn>
            <GoogleBtn
                clientId={'628315189039-kira075j0fog81kb2kua27l8ubtlg0a4.apps.googleusercontent.com'}
                buttonText="구글로그인"
                onSuccess={responseGoogle}
                onFailure={responseFail}
            ></GoogleBtn>
            <Button>구글 로그인</Button>
        </div>
    );
};

export default LoginFrom;
