import React, { useState, useCallback } from 'react';
import { Button, Form, Result } from 'antd';
import KakaoLogin from 'react-kakao-login';
import GoogleLogin from 'react-google-login';
// import NaverLogin from 'react-naver-login';
import NaverLogin from 'react-login-by-naver';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { socailLoginRequest } from '../reducers/user';


const KaKaoBtn = styled(KakaoLogin)`
    width: 300px;
    height: 50px;
    background-color: #f7f30c;
    font-weight: 1000;
`;

const NaverBtn = styled(Button)`
    width: 300px;
    height: 50px;
    background-color: #10cc23;
    font-weight: 1000;
    color: #ffffff;
`;

const GoogleBtn = styled(GoogleLogin)`
    width: 300px;
    height: 50px;
    
`;


const LoginWrapper = styled(Form)`
    padding: 10px;
`;

const LoginFrom = () => {
    const userId = 1;
    const nickname = '임시 닉네임';

    const dispatch = useDispatch();


    // const [id, setId] = useState('');

    const responseKakao = (response) => {
        console.log(response.response.access_token);
        const data = { accessToken: response.response.access_token, platform: 'kakao' };
        dispatch(socailLoginRequest(data));
    };

    const responseNaver = (response) => {
        if(typeof window !== 'undefined'){
            if((window.location.origin + '/') != window.location.href){
                const location = window.location.href.split('=')[1];
                const accessToken = location.split('&')[0];
                const data = {accessToken : accessToken, platform: 'naver'};
                dispatch(socailLoginRequest(data));
            }
        }
    }

    const responseGoogle = (response) => {
        console.log(response.accessToken)
        const data = { accessToken: response.accessToken, platform: 'google' };
        dispatch(socailLoginRequest(data));
    };

    const responseFail = (err) => {
        console.error(err);
        
    };

    return (
        <LoginWrapper>
            <KaKaoBtn
                jsKey={'593246069c7b7a42344710a951c039ee'}
                buttonText="카카오로그인"
                onSuccess={responseKakao}
                onFailure={responseFail}
                getProfile={true}
            ></KaKaoBtn>
            <br />
            <NaverLogin
                clientId="0xe5BaDwdA6UNdboCXn7"
                callbackUrl="http://127.0.0.1:3000/"
                render={(props) => <NaverButton onClick={props.onClick}><img
                src={naver}
                alt="naver"
                style={{
                  width: '100%',
                  height: '100%',
                }}
              /></NaverButton>}
              onSuccess={(res) => responseLogin(res, 'naver')}
              onFailure={() => console.log('naver login fail')}
            />
            <br />
            <GoogleBtn
                clientId={'39496035317-r14irfnovjild7jovff5n0grpthkb206.apps.googleusercontent.com'}
                buttonText="구글로그인"
                onSuccess={responseGoogle}
                onFailure={responseFail}
            ></GoogleBtn>
            <br />
        </LoginWrapper>
    );
};

export default LoginFrom;
