import React from 'react';
import PropTypes from 'prop-types';     // prop-types 모듈 사용
import Link from 'next/link';
import { Menu, Row, Col } from 'antd';            // https://ant.design/components/menu/
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { Button } from 'antd';

import LOGO from '../images/WEB_LOGO.png'
import APP_STORE from '../images/APP_STORE.png'
import PLAY_STORE from '../images/PLAY_STORE.png'

const Background = styled.div`
    width: 100%;
    height: 100vh;
    background: #FA5A5A;  /* fallback for old browsers */
    background: -webkit-linear-gradient(to right, #f80759, #FA5A5A);  /* Chrome 10-25, Safari 5.1-6 */
    background: linear-gradient(to right, #f80759, #FA5A5A); /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
`

const LoginBtn = styled(Button)`
    height: 3vh;
    width: 40%;
`
const MyMenu = styled.ul`
    list-style-type: none;
`
const APP = () => {
    return (
        <Background>
            <Row>
                <Col xs={24} md={12} >
                    <LoginBtn>로그인</LoginBtn>
                </Col>

                <Col xs={24} md={12} >
                    <div>
                        <img src={APP_STORE} style={{ float: 'right'}}></img>
                        <img src={PLAY_STORE} style={{ float: 'right' }}></img>
                        {/* <img src={LOGO} style={{ height: "100vh", position: 'absolute', right: 0 }}></img> */}
                        <nav>
                            <a href="#tag1" style={{ color: 'white', display: 'inline', marginLeft: '50px', fontSize: '2vh'}}>HOME</a>
                            <a style={{ color: 'white', display: 'inline', marginLeft: '50px', fontSize: '2vh'}}>ABOUT</a>
                            <a style={{ color: 'white', display: 'inline', marginLeft: '50px', fontSize: '2vh'}}>CHARACTOR</a>
                            <a style={{ color: 'white', display: 'inline', marginLeft: '50px', fontSize: '2vh'}}>HELP</a>
                        </nav>
                    </div>
                </Col>
            </Row>

        </Background>
    );
};


export default APP;
