import React from 'react';
import PropTypes from 'prop-types';     // prop-types 모듈 사용
import Link from 'next/link';
import { Menu, Row, Col } from 'antd';            // https://ant.design/components/menu/
import styled from 'styled-components';
import { useSelector } from 'react-redux';
import { Button } from 'antd';

import LOGO from '../images/WEB_LOGO.png'
import APP_STORE from '../images/APP_STORE.png'
import PLAY_STORE from '../images/PLAY_STORE.png'
import GALAXY from '../images/galaxy.png'
import IPHONE from '../images/iphone.png'


const Background = styled.div`
    width: 100%;
    height: 100vh;
    background: #FA5A5A;  /* fallback for old browsers */
    background: -webkit-linear-gradient(to right, #f80759, #FA5A5A);  /* Chrome 10-25, Safari 5.1-6 */
    background: linear-gradient(to right, #f80759, #FA5A5A); /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
`

const LoginBtn = styled(Button)`
    width: 100%;
    height: 4vh;

`

const APP = () => {
    return (
        <Background id="home">
                <LoginBtn style={{fontSize: "2vh" }}><a href="/login">로그인</a></LoginBtn>
            <nav style={{ position: "absolute", top: "5%", right: "20VH", width: '20%' }}>
                <a href="#home" style={{ color: 'white', display: 'inline', fontSize: '100%' }}>HOME</a>
                <a href="#about" style={{ color: 'white', display: 'inline', marginLeft: '20%', fontSize: '100%'}}>ABOUT</a>
                <a href="#charactor" style={{ color: 'white', display: 'inline', marginLeft: '20%', fontSize: '100%' }}>CHARACTOR</a>
                <a href="#help" style={{ color: 'white', display: 'inline', marginLeft: '20%', fontSize: '100%' }}>HELP</a>
            </nav>
        </Background>
    );
};


export default APP;
