import React, { useEffect } from 'react';
import { Button, List, Comment, Popover } from 'antd';
import styled from 'styled-components';
import CommentForm from './CommentForm';
import { useDispatch, useSelector } from 'react-redux';
import { loadCommentsRequestAction } from '../reducers/comment';
import {detailLolPostOffAction, detailLolPostOnAction} from '../reducers/post';

const Frame = styled.div`
    border: 1px solid #000000;
`;

const DetailPost = () => {
    const { me } = useSelector((state) => state.user);
    const {detailLolPost} = useSelector((state) => state.post);
    const { comments } = useSelector((state) => state.comment);
    const dispatch = useDispatch();

    useEffect(() => {
        if (!detailLolPost.id) {
            const data = { userToken: me.userToken, postId: data.postId };
            dispatch(loadCommentsRequestAction(data));
        }
    }, []);

    const onClose = () => {
        dispatch(detailLolPostOffAction())
    }

    return (
        <Frame style={{ width: '100%', padding: '20px' }}>
            <Button style={{ width: '100%' }} onClick={onClose} type="primary">닫기</Button>
            <div style={{ fontSize: 20 }}>{detailLolPost.title}</div>
            <div>{detailLolPost.content}</div>
            <CommentForm />
            <List
                header={`${comments ? comments.length : 0}개의 댓글`}
                itemLayout="horizontal"
                dataSource={comments}
                renderItem={(item) => (
                    <li>
                        <Comment
                            author={item.nickname}
                            content={item.content}
                        />
                    </li>
                )}
            >
            </List>
        </Frame>
    );
};

export default DetailPost;
