import React, { useCallback, useEffect } from 'react';
import { Button, Form, Input } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { addCommentRequestAction } from '../reducers/comment';

const CommentForm = () => {

    const { me } = useSelector((state) => state.user);
    const { detailLolPost } = useSelector((state) => state.post);
    const dispatch = useDispatch();
    const [content, setContent] = useState('');

    const onChangeComment = useCallback((e) => {
        setContent(e.target.value);
    });


    const onAddComment = useCallback(() => {
        const data = { content: content, userId: me.userId, postId: detailLolPost.id, nickname: me.nickname };
        dispatch(addCommentRequestAction(data));
        setContent('');
    }, []);

    return (
        <>
            <Form>
                <Form.Item style={{ position: 'relative' }}>
                    <Input.TextArea onChange={onChangeComment}></Input.TextArea>
                    <Button onClick={onAddComment} style={{ position: 'absolute', right: 0, bottom: -40 }} htmlType='submit'>제출</Button>
                </Form.Item>
            </Form>
        </>

    )
};



export default CommentForm;
