import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import { Card } from 'antd';
import { ArrowDownOutlined } from '@ant-design/icons';
import DetailPost from './DetailPost';
import { useDispatch, useSelector } from 'react-redux';
import { detailLolPostOnAction } from '../reducers/post';


const CardWrapper = styled.div`
    margin: 20px;
    display: inline-block;
    width: 400px;
`;

const CardMeta = styled(Card.Meta)`
    height: 150px;
`;

const LolPostCard = ({ post }) => {

    const { me } = useSelector((state) => state.user);
    const { posts, loadPostLoading, loadPostError, detailLolPost } = useSelector((state) => state.post);
    const dispatch = useDispatch();

    const onClickMore = useCallback(() => {
        dispatch(detailLolPostOnAction(post));
    }, []);

    // const onGetComments = useCallback(() => {
    //     const data = { userToken: me.userToken, postId: detailLolPost.id };
    //     dispatch(loadCommentsRequestAction(data));
    // }, []);

    return (
        <>
            <CardWrapper key={post.id}>
                <Card
                    actions={[<ArrowDownOutlined key="more" onClick={onClickMore, onGetComments} />]}
                    bordered={true}
                ><CardMeta
                        title={post.title}
                        description={post.content}
                    />
                </Card>
            </CardWrapper>
        </>
    );
};


export default LolPostCard;
