import { Button } from 'antd';
import React, { useEffect } from 'react';
import AppLayout from '../components/AppLayout';
import Link from 'next/link';
import { useSelector, useDispatch } from 'react-redux';
import SignUpForm from '../components/SignUpForm';
import { loadAllLolPostsRequestAction, LOAD_ALL_LOL_POSTS_REQUEST } from '../reducers/post';
import { loginRequestAction } from '../reducers/user';

import LoginFrom from '../components/LoginForm';


const Home = () => {
    const dispatch = useDispatch();
    const { me } = useSelector((state) => state.user);
    const { logInError } = useSelector((state) => state.user);
    const { posts, loadPostLoading, loadPostError } = useSelector((state) => state.post);


    const click = () => {
        if (me.userToken != null) {
            console.log(me.userToken);
            const data = {userToken: me.userToken};
            dispatch(loginRequestAction(data));
        }
    }


    if (logInError === -401) {
        return (
            <AppLayout>
                <SignUpForm />
            </AppLayout>
        )
    } else {
        return (
            <AppLayout>
                <Button type="primary" style={{ marginLeft: '20px' }}><Link href="./lol_write"><a>글쓰기</a></Link></Button>
                <Button type="primary" onClick={click}>테스트</Button>

                {me.userId
                    ? posts.map((c) => {return (<LolPostCard key={c.id} post={c} />);})
                    : <div>로그인이 필요합니다.</div>
                }
            </AppLayout>
        );
    }
};

export default Home;
