export const initialState = {
    isLoggedIn: false,
    userId: null,
    nickname: null,
}

const LOG_IN = 'LOG_IN';
const LOG_OUT = 'LOG_OUT';

export const loginAction = (data) => {
    return {
        type: LOG_IN,
        data,
    }
}

export const logoutAction = () => {
    return {
        type: LOG_OUT,
    }
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case LOG_IN:
            return {
                ...state,
                isLoggedIn: true,
                userId: action.data.userId,
                nickname: action.data.nickname,
            }
        case LOG_OUT:
            return {
                ...state,
                isLoggedIn: false,
                userId: null,
                nickname: null,
            }
    }
}

export default reducer;
