import { all, fork, put, takeLatest } from "redux-saga/effects";
import axios from 'axios';
import { useSelector } from 'react-redux';


function socailLogInAPI() {
    
    const { platform, accessToken } = useSelector((state) => state.user);

    console.log("hi", platform, accessToken);

}


function* logIn(action) {
    try {
        const result = yield call(socailLogInAPI);
        
        console.log("된다");

        // yield put({
        //     type: 'LOG_IN_SUCCESS',
        //     data: action.data,
        // });

    } catch (err) {
        console.log("안된다.");
        // yield put({
        //     type: 'LOG_IN_FAILURE',
        //     error: action.response.data,
        // });
    }
}

function* watchLogIn() {
    yield takeLatest('SOCIAL_LOG_IN_REQUEST', logIn);
}

export default function* userSaga() {
    yield all([
        fork(watchLogIn),
    ]);
}
