import React, { useEffect } from 'react';
import { Button, List, Comment, Popover } from 'antd';
import styled from 'styled-components';
import CommentForm from './CommentForm';
import { useDispatch, useSelector } from 'react-redux';
import { loadCommentsRequestAction } from '../reducers/comment'

const Frame = styled.div`
    border: 1px solid #000000;
`;

const DetailPost = ({ onClose, data }) => {
    const { me } = useSelector((state) => state.user);
    const { comments } = useSelector((state) => state.comment);
    const dispatch = useDispatch();

    const po = [];

    // useEffect(() => {
    //     if (!me.userToken) {
    //         console.log('확인확인');
    //         const data2 = { userToken: me.userToken, postId: data.postId };
    //         dispatch(loadCommentsRequestAction(data2));
    //     }
    // }, []);

    const checkComments = () => {
        console.log('확인확인');
        const data2 = { userToken: me.userToken, postId: data.id };
        dispatch(loadCommentsRequestAction(data2));
    }

    if(comments[0] !== null && comments[0].postId === data.id){
        po = comments;
    }

    return (
        <Frame style={{ width: '100%', padding: '20px' }}>
            <Button style={{ width: '100%' }} onClick={checkComments} type="primary">닫기</Button>
            <div style={{ fontSize: 20 }}>{data.title}</div>
            <div>{data.content}</div>
            <CommentForm />
            <List
                header={`${comments ? comments.length : 0}개의 댓글`}
                itemLayout="horizontal"
                dataSource={po}
                renderItem={(item) => (
                    <li>
                        <Comment
                            author={item.nickname}
                            content={item.content}
                        />
                    </li>
                )}
            >
            </List>
        </Frame>
    );
};

export default DetailPost;
