export const initialState = {
    isLoggedIn: false,
};

export const LOG_IN = 'LOG_IN'; // 액션의 이름
export const LOG_OUT = 'LOG_OUT';

export const loginAction = (data) => {
    return {
        type: LOG_IN,
        data,
    }
};

export const logoutAction = {
    type: LOG_OUT,
};

export default (state = initialState, action) => {
    switch (action.type) {
        case LOG_IN: {
            return {
                ...state,
                isLoggedIn: true,
            };
        }
        case LOG_OUT: {
            return {
                ...state,
                isLoggedIn: false,
            };
        }
    }
};
