import { Button } from 'antd';
import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import AppLayout from '../components/AppLayout';
import LolPostCard from '../components/LolPostCard';
import Link from 'next/link';


const Home = () => {
    const { mainPosts } = useSelector((state) => state.post);
    const { userId } = useSelector((state) => state.user);


    
    if(typeof window !== 'undefined'){
        if((window.location.origin + '/') != window.location.href){

            const dispatch = useDispatch();
            const location = window.location.href.split('=')[1];
            const accessToken = location.split('&')[0];
            console.log(accessToken);

            
            

            
        }

    }

    // const location = window.location.href.split('=')[1];
    // const accessToken = location.split('&')[0];


    

    return (
        <AppLayout>
            {userId
                ? <Button type="primary" style={{ marginLeft: '20px' }}><Link href="./lol_write"><a>글쓰기</a></Link></Button>
                : <></>}
            <br />

            {userId
                ? mainPosts.map((c) => { return (<LolPostCard key={c.id} post={c} />); })
                : <div>로그인이 필요합니다.</div>}
        </AppLayout>

    );

};

export default Home;
