export const initialState = {
    posts:[],
};

export const getAllLoLPostRequest = () => {
    return {    
        type : 'GET_ALL_LOL_POST_REQUEST',
    }
}


const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'GET_ALL_LOL_POST_REQUEST':
            return {
                ...state,
            };
        case 'GET_ALL_LOL_POST_SUCCESS':
            return {
                ...state,
                posts: action.data
            };
        default:
            return state;
    }
};

export default reducer;
