    } catch (err) {

        console.error(err);
        yield put({
            type: 'LOG_IN_FAILURE',
            error: action.response.data,
        });

        
    }
}

function* watchLogIn() {
    yield takeLatest('SOCIAL_LOG_IN_REQUEST', logIn);
}

export default function* userSaga() {
    yield all([
        fork(watchLogIn),
    ]);
}
