import React, { useCallback, useState } from 'react';
import { Button, Input } from 'antd';
import { useSelector } from 'react-redux';

const MyInfo = () => {

    const userNickname = useSelector((state) => state.user.userNickname);
    const [isChangeNickname, setIsChangeNickname] = useState(false);
    const onIsChangeNickname = useCallback(() => {
        setIsChangeNickname(true)
    })

    return (
        <>
            {isChangeNickname ? <Input prefix={userNickname}></Input> : <Input prefix={userNickname} suffix={<Button onClick={onIsChangeNickname}>변경</Button>} disabled />}

        </>
    );
}

export default MyInfo;
