                style={{ marginBottom: 20 }}
                grid={{ gutter: 4, xs: 2, md: 3 }}
                size="large"
                header="글 목록"
                pagination={{
                    onChange: page => {

                    },
                    pageSize: 3,
                }}
                bordered
                dataSource={data}
                renderItem={(itme) => (
                    <List.Item style={{ marginTop: 20 }}>
                        <Card actions={[itme.content]}>
                            <Card.Meta description={item.title} />
                        </Card>
                    </List.Item>
                )}
            />
        </>
    );
};


